// dynamically set drift robot position

setInterval(function(){
  var minHeight = 1;
  if ($("#onetrust-banner-sdk").height() > minHeight) {
    var bannerHeight = $('#onetrust-banner-sdk').height();
    $("#drift-widget").css({"bottom": bannerHeight, "margin-bottom": "20px"});
  } else {
    $("#drift-widget").css("bottom", "24px");
  }
  $("#onetrust-button-group button").click(function() {
    $("#drift-widget").css("bottom", "24px");
    $("#onetrust-banner-sdk").css({"height": 0, "display": "none"})
  });
}, 500);
